import React, {Fragment} from "react";
import bannerpic1 from "../img/bannerpic1.svg"
import Container from "react-bootstrap/Container";
import styled from "styled-components";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";


const Styles = styled.div`
  .main-special {
    position: relative;
    height: 36vh;
    width: 100%;
    background: #567786;
    cursor: pointer;
    overflow: hidden;
    // padding-top: 2%;
    // padding-bottom: 2%;
  }
  .header {
  }
  .textContainer {
    justify-content: center;
    padding-top: 5%;
    padding-bottom: 5%;
  }
  .text {
    font-family: "Open Sans";
  }
`;

const Banner = (props) => {
    const {imageurl, backgroundPos } = props;

  return (
      <Fragment>
    <Styles> 
   <div className="header"> 
   <Container fluid 
   style={{height:"45vh", backgroundImage:`${imageurl}`,backgroundPosition: `${backgroundPos}`}}
   > 
    <Row style={{paddingTop:"7rem"}}>
    <Col xs={0} sm={0} md={1} lg={0} xl={2}></Col>
       <h1 style={{fontSize:"65px", fontFamily:"Roboto",fontWeight:"bold",color:"white"}}> TravelBuddy </h1> 
       </Row>

       <Row>
       <Col xs={0} sm={0} md={1} lg={0} xl={2}></Col>
       <h3 style={{fontSize:"33px", fontFamily:"Roboto",fontWeight:"bold",color:"white"}}> The internet's source for the <u style={{color:"#D7D7D7"}}> best flights.</u> </h3> 
       </Row>


  </Container>
   </div>
   </Styles> 

   </Fragment>
  );
};

export default Banner;
