import React, { Fragment, useState } from "react";
import { withStyles } from "@material-ui/core/styles";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";
import Card from "react-bootstrap/Card";
import Badge from "react-bootstrap/Badge";
import AccessAlarmsIcon from "@material-ui/icons/AccessAlarms";
import GitHubIcon from "@material-ui/icons/GitHub";
import WebIcon from "@material-ui/icons/Web";
import Button from "react-bootstrap/Button"

import {AiFillStar,AiOutlineStar} from "react-icons/ai"
import {FaPlaneDeparture,FaPlaneArrival} from "react-icons/fa"

import Accordion from 'react-bootstrap/Accordion'

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
});

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;

  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 1,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions);

export const ListingDialog = (props) => {
  var tags = props.tags;
  var link = props.link;
  var web = props.web;

  let tagsToRender;
  if (tags) {
    tagsToRender = tags.map((tag) => {
      return (
        <Badge pill variant="secondary" style={{ marginRight: "3px" }}>
          {tag}
        </Badge>
      );
    });
  }

  if (!link) {
    link = "https://github.com/glendont";
  }


  return (
    <Fragment>
      <Dialog
        fullWidth={Boolean(true)}
        maxWidth="sm"
        onClose={props.close}
        open={props.state}
        id="dialog-box-div"
      >
        {/* <DialogContent dividers id="dialog-content-div"> */}
          <Card style={{ margin:"-20px", overflowY:"hidden"}}>
            <Card.Img
              variant="top"
              src={props.image}
              style={{ width: "100%", height: "30vh" }}
            />
            <Card.Body>

            <b style={{float:"left", opacity:"0.7"}}>  
                    <AiFillStar/>
                    <AiFillStar/>
                    <AiFillStar/>
                    <AiFillStar/>
                    <AiOutlineStar/>
            </b>

              <p

                style={{ fontSize: "12px", textAlign: "right" }}
              >
                <AccessAlarmsIcon
                  fontSize="small"
                  style={{
                    fontSize: "15px",
                    marginBottom: "2px",
                    marginRight: "3px",
                  }}
                />
               Deal Last Updated: {"  "}{" "}
                {props.date ? (
                  props.date
                ) : (
                  <p style={{ display: "inline" }}> 13 June 2020 </p>
                )}


              </p>{" "}
              <Card.Title>
                {" "}
                <h2 className="lead" style={{fontFamily:"Sans Serif"}}>

                  <b style={{float:"left"}}> <FaPlaneDeparture/> Singapore </b> 
                    <br/> 
                  <b style={{float:"left", marginTop:"12px"}}> <FaPlaneArrival/> New York, United States  </b>

                  <Accordion defaultActiveKey="0">
                  <Accordion.Toggle as={Button} variant="link" eventKey="1" style={{float:'right'}}><b> Click</b></Accordion.Toggle>


                    </Accordion>


                </h2>

                <Accordion.Collapse eventKey="1">
                <Card.Body style={{float:'left'}}>Hello! I'm another body</Card.Body>
                </Accordion.Collapse>
              </Card.Title>
              <Card.Text className="lead" style={{ paddingLeft: "5px" }}>
{/* 
<Accordion defaultActiveKey="0">

      <Accordion.Toggle as={Button} variant="link" eventKey="1">
        Click me!
      </Accordion.Toggle>

    <Accordion.Collapse eventKey="1">
      <Card.Body>Hello! I'm another body</Card.Body>
    </Accordion.Collapse>

</Accordion> */}


                <p className="lead">
                    <br/> 
                  <b> Technologies</b>
                </p>
                <b>{tagsToRender}</b> <hr></hr>
              </Card.Text>

              <Card.Text
                className="lead"
                style={{ fontSize: "15px", letterSpacing: "0.04em" }}
              >
                <div style={{ marginBottom: "30px" }}>{props.desc}</div>


                <Button variant="outlined" size="sm" style={{height:"35px",float:'right', width:'120px',backgroundColor:'#01A799'}}> <p style={{fontSize:"15px", fontWeight:"bolder", fontColor:"#767676", color:"white"}}> <center> Add to Cart </center>  </p></Button>        


              </Card.Text>
            </Card.Body>
          </Card>
        {/* </DialogContent> */}
      </Dialog>
    </Fragment>
  );
};
