import React, {setState, useState} from "react";
import { Link } from "react-router-dom";
import LocalAirportIcon from '@material-ui/icons/LocalAirport';
import ApartmentIcon from '@material-ui/icons/Apartment';
import LoyaltyIcon from '@material-ui/icons/Loyalty';

const SegmentBar = () => {


  const [SpecialClassName, setSpecialClassName] = useState('deactivated-tab')
  const [FlightCassName, setFlightCassName] = useState('deactivated-tab')
  const [HotelClassName, setHotelClassName] = useState('deactivated-tab')


  const changeSpecialClass=()=>{
    setSpecialClassName('activated-tab')
    setFlightCassName('deactivated-tab')
    setHotelClassName('deactivated-tab')
  }


return (

    <nav className="navbar">
      <ul className="segment-links">
      <li className="segment-links-specials">
          <Link to="/home" onClick={changeSpecialClass} className={SpecialClassName} >
          <LoyaltyIcon fontSize="small" style={{marginBottom:"3px", opacity:`${localopacityspecials}`}}/> {' '}
            Specials  |
          </Link>{" "}
        </li>
        <li className="segment-links-Flights">
        <Link to="/flight" onClick={changeOpacityFlight} style={{opacity:`${localopacityflight}`}}>

              <LocalAirportIcon fontSize="small" style={{marginBottom:"2px", opacity:'0.85'}}/> {' '}
            Flights 
          </Link>{" "}
        </li>
        <li className="segment-links-Hotels">
        <Link to="/hotel" onClick={changeOpacityHotel} style={{opacity:`${localopacityhotel}`}}>

          <ApartmentIcon fontSize="small" style={{marginBottom:"4px", opacity:'0.85'}}/> {' '}
            Hotels 
          </Link>{" "}
        </li>

      </ul>


    </nav>
)

}

export default SegmentBar;
