import React, {Fragment,useState, setState} from "react"
import Card from 'react-bootstrap/Card'
import bannerpalmtrees from "../img/bannerpalmtrees.jpg"

import { useHoverLeave } from "../function/useHoverLeave"



const HotelSpecialsCard = () => {


    // const [ref, hovered] = useHoverLeave();
    const [mainClassName, setmainClassName] = useState("hotel-specials-img-1")

    const [hovered,setHovered] = useState(true)
    const mouseOver = () =>{ 
        setHovered(false)
    }
    const mouseOut = () =>{ 
        setHovered(true)
    }

return (
    <div> 
        <Card
          border="light"
          className="hotel-specials-card">


{hovered ? (

<div className="hotel-specials-img-1" onMouseOver={mouseOver} onMouseOut={mouseOut}> </div> 
) : (
    <div className="hotel-specials-img-2" onMouseOver={mouseOver} onMouseOut={mouseOut} >
        <img 
        style={{height:"240px",backgroundSize:"400px"}}
        src={bannerpalmtrees}
        /> 

<h1 className="hovered-text-primary"> <center> Infinity Pool, Infinity Fun! </center></h1> 

<h1 className="hovered-text-secondary"> Singapore Airlines </h1> 


    </div> 

    )}


          <Card.Body style={{backgroundColor:"#1A3752"}}>
            <Card.Title>
              <h1 className="hotel-box-primary-text">
                {"\n"}
                Finns Beach Resort <br/> Singapore
              </h1>{" "}
              <h5 style={{float:'right'}}> <span class="badge badge-pill badge-success box-secondary-badge" style={{backgroundColor:'#017864'}}> 41 minutes to go! </span> <br/> <p className="hotel-box-secondary-text" style={{float:"right"}}>  $201 / night</p> </h5>
            </Card.Title>
          </Card.Body>
        </Card>
</div>
)
}

export default HotelSpecialsCard;

