import React, {Fragment} from "react"
import Card from 'react-bootstrap/Card'
import Button from "react-bootstrap/Button"
import bannerpalmtrees from "../img/bannerpalmtrees.jpg"

const CardTrial = () => {
return (
    <Fragment>
<div className={mainClassName} ref={ref}>
      <ProjectDialog
        title={props.title}
        link={props.link}
        desc={description}
        short_desc={short_desc}
        para2={props.para2}
        para3={props.para3}
        state={open}
        close={handleClose}
        image={props.image}
        tags={props.tags}
        web={props.web}
      />
      {hovered ? (
        <div className="projectWordedDiv" style={{ height: "230px" }}>
          <div className="innerNameFlex animated zoomIn">
            <div className="alignTextDiv">
              <p
                className="lead ProjectNameWords"
                style={{ marginTop: "1rem" }}
              >
                <b style={{ font: "Roboto" }}> {title}</b>
              </p>
              <p className="ProjectDescWords">{short_desc}</p>
            </div>
          </div>

          <div className="innerButtonFlex animated fadeIn">
            <Button
              onClick={handleOpen}
              variant="outline-secondary"
              style={{ height: "2.5rem", marginBottom: "6rem" }}
            >
              <p>Learn More</p>
            </Button>
          </div>
        </div>
      ) : (
        <div className="projectImageDiv animated fadeIn">
          <img
            style={{
              minWidth: "100%",
              width: "100%",
              maxWidth: "100%",
              height: "230px",
            }}
            src={image}
            alt={title}
          />
        </div>
      )}
    </div>
  </Fragment>
)
}

export default CardTrial;
