import React, {setState, useState} from "react";
import { Link } from "react-router-dom";
import LocalAirportIcon from '@material-ui/icons/LocalAirport';
import ApartmentIcon from '@material-ui/icons/Apartment';
// import HotelIcon from '@material-ui/icons/Hotel';
import LoyaltyIcon from '@material-ui/icons/Loyalty';

const SegmentBar = () => {

  const [localopacityspecials, setLocalopacityspecials] = useState(0.6)
  const [localopacityflight, setLocalopacityflight] = useState(0.6)
  const [localopacityhotel, setLocalopacityhotel] = useState(0.6)

  const changeOpacitySpecials = () => {
    localopacityspecials(1.0)
    setLocalopacityflight(0.6)
    setLocalopacityhotel(0.6)
  }
  const changeOpacityFlight = () => {
    setLocalopacityflight(1.0)
  }
  const changeOpacityHotel = () => {
    setLocalopacityhotel(1.0)
  }

return (

    <nav className="navbar">
      <ul className="segment-links">
      <li className="segment-links-specials">
          <Link to="/home" onClick={changeOpacitySpecials} style={{opacity:`${localopacityspecials}`}}>
          <LoyaltyIcon fontSize="small" style={{marginBottom:"3px", opacity:`${localopacityspecials}`}}/> {' '}
            Specials  |
          </Link>{" "}
        </li>
        <li className="segment-links-Flights">
          <Link to="/flight" activeClassName="currentlyOn">
              <LocalAirportIcon fontSize="small" style={{marginBottom:"2px", opacity:'0.85'}}/> {' '}
            Flights 
          </Link>{" "}
        </li>
        <li className="segment-links-Hotels">
          <Link to="/hotel" activeClassName="currentlyOn">
          <ApartmentIcon fontSize="small" style={{marginBottom:"4px", opacity:'0.85'}}/> {' '}
            Hotels 
          </Link>{" "}
        </li>

      </ul>


    </nav>
)

}

export default SegmentBar;
