import React, { Fragment, useState } from "react";
import { withStyles } from "@material-ui/core/styles";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
import MuiDialogContent from "@material-ui/core/DialogContent";
import MuiDialogActions from "@material-ui/core/DialogActions";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Typography from "@material-ui/core/Typography";
import Card from "react-bootstrap/Card";
import Badge from "react-bootstrap/Badge";
import AccessAlarmsIcon from "@material-ui/icons/AccessAlarms";

import Button from "react-bootstrap/Button"

import {AiFillStar,AiOutlineStar} from "react-icons/ai"
import {FaPlaneDeparture,FaPlaneArrival} from "react-icons/fa"

import Accordion from 'react-bootstrap/Accordion'

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: "absolute",
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },
});

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;

  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton
          aria-label="close"
          className={classes.closeButton}
          onClick={onClose}
        >
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
  root: {
    margin: 1,
    padding: theme.spacing(1),
  },
}))(MuiDialogActions);

export const ListingDialog = (props) => {
  var tags = props.tags;
  var link = props.link;
  var web = props.web;

  let tagsToRender;
  if (tags) {
    tagsToRender = tags.map((tag) => {
      return (
        <Badge pill variant="secondary" style={{ marginRight: "3px" }}>
          {tag}
        </Badge>
      );
    });
  }

  if (!link) {
    link = "https://github.com/glendont";
  }


  return (
    <Fragment>
      <Dialog
        fullWidth={Boolean(true)}
        maxWidth="sm"
        onClose={props.close}
        open={props.state}
        id="dialog-box-div"
      >
        {/* <DialogContent dividers id="dialog-content-div"> */}
          <Card style={{ margin:"-20px", overflowY:"hidden"}}>
            <Card.Img
              variant="top"
              src={props.image}
              style={{ width: "100%", height: "30vh" }}
            />
            <Card.Body>

            <b style={{float:"left", opacity:"0.7"}}> 

                    <AiFillStar/>
                    <AiFillStar/>
                    <AiFillStar/>
                    <AiFillStar/>
                    <AiOutlineStar/>
                    <b style={{fontSize:"11px", marginLeft:'2px'}}> 1281 reviews </b> 


            </b>

              <p

                style={{ fontSize: "12px", textAlign: "right" }}
              >
                <AccessAlarmsIcon
                  fontSize="small"
                  style={{
                    fontSize: "15px",
                    marginBottom: "2px",
                    marginRight: "3px",
                  }}
                />
               Deal Last Updated: {"  "}{" "}
                {props.date ? (
                  props.date
                ) : (
                  <p style={{ display: "inline" }}> 13 June 2020 </p>
                )}


              </p>{" "}

              <Accordion defaultActiveKey="0">
                    <Card >
                        <Card.Header style={{margin:"-17px -20px -17px -20px"}}>
                        <Accordion.Toggle as={Card.Header} eventKey="1" style={{color:"black", fontFamily:"Roboto", fontSize:"18px", backgroundColor:"#F7F7F7", border:"none"}}> 

                        <b style={{float:"left", marginBottom:"12px"}}> <FaPlaneDeparture style={{marginRight:"10px"}}/> Singapore <br/> 
                    <FaPlaneArrival style={{marginRight:"10px"}}/> New York, United States  </b>
                        </Accordion.Toggle>
                        </Card.Header>
                        <Accordion.Collapse eventKey="1">

                        <Card.Body className="lead" style={{marginTop:"20px"}}> 
                          <p style={{fontSize:"12px",fontWeight:"bold"}}>  Singapore Airlines QR702 </p>
                          <ul style={{lineHeight:"50px"}}>

                            <li> 17:35 JFK New York John F. Kenndy </li> 
                            <li> 20:30 JFK New York John F. Kenndy </li> 
                            <li> 09:15 JFK New York John F. Kenndy </li> 

                          </ul>

                          <p style={{fontSize:"12px", float:"left", marginBottom:'-20px'}}>  <b> Arrives: </b> Tue, 3 Nov 2020 | <b>Journey Duration:</b> 23h 15 </p>



                          </Card.Body>
                        </Accordion.Collapse>
                    </Card>
                    </Accordion>

              <Card.Text
                className="lead"
                style={{ fontSize: "15px", letterSpacing: "0.04em" }}
              >
                <div style={{ marginBottom: "30px"}}>{props.desc}</div>


                <Button variant="outlined" size="sm" style={{height:"35px",float:'right', width:'120px',backgroundColor:'#01A799'}}> <p style={{fontSize:"15px", fontWeight:"bolder", fontColor:"#767676", color:"white"}}> <center> Add to Cart </center>  </p></Button>        


              </Card.Text>
            </Card.Body>
          </Card>
        {/* </DialogContent> */}
      </Dialog>
    </Fragment>
  );
};
